import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

// Import der Modellklassen
import { Vehicle } from '../models/Vehicle';
import { Car } from '../models/Car';
import { Truck } from '../models/Truck';
import { Motorcycle } from '../models/Motorcycle';
import { Caravan } from '../models/Caravan';

@Component({
  selector: 'app-vehicle',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './vehicle.component.html',
  styleUrls: ['./vehicle.component.css']
})
export class VehicleComponent {
  searchTerm: string = ''; // Variable für Two-Way-Datenbindung

  filters = {
    brand: '',
    model: '',
    maxPrice: null as number | null,
    maxMileage: null as number | null,
    minYear: null as number | null
  };

  // Fahrzeuge als Objekte der Modellklassen
  cars: Car[] = [
    new Car(1, 'BMW', 'X5', 60000, 20000, new Date(2019, 5, 10), 'Diesel', 300, 'Luxury SUV', 'https://stimg.cardekho.com/images/carexteriorimages/930x620/BMW/X5-2023/10452/1688992642182/front-left-side-47.jpg',
     4, 5, 'SUV', 'Gebrauchtwagen', true, 'Automatik', 'Allrad', 'Schwarz'),
    new Car(2, 'Mercedes', 'C-Class', 45000, 15000, new Date(2020, 3, 15), 'Benzin', 200, 'Elegant Sedan', 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/Mercedes-Benz_C_220_BlueTEC_Exclusive_%28W_205%29_%E2%80%93_Frontansicht%2C_12._Juli_2014%2C_D%C3%BCsseldorf.jpg/1200px-Mercedes-Benz_C_220_BlueTEC_Exclusive_%28W_205%29_%E2%80%93_Frontansicht%2C_12._Juli_2014%2C_D%C3%BCsseldorf.jpg',
     4, 5, 'Limousine', 'Gebrauchtwagen', true, 'Automatik', 'Heckantrieb', 'Weiß'),
    new Car(3, 'Tesla', 'Model S', 80000, 10000, new Date(2021, 1, 20), 'Elektro', 500, 'Electric Performance Car', 'https://www.greendrive-accessories.com/blog/wp-content/uploads/2023/09/Tesla-Model-3-uplift-Red.jpg',
      4, 5, 'Limousine', 'Neuwagen', true, 'Automatik', 'Allrad', 'Rot', 100, 600)
  ];

  trucks: Truck[] = [
    new Truck(1, 'Volvo', 'FH16', 120000, 50000, new Date(2018, 8, 5), 'Diesel', 750, 'Heavy Duty Truck', 'data:image/jpeg;base64,', 'LKW über 3,5t', 'Gebrauchtwagen', true, 'Manuell', 'Allrad', 'Blau'),
    new Truck(2, 'MAN', 'TGX', 110000, 40000, new Date(2019, 10, 12), 'Diesel', 640, 'Efficient Truck', 'https://www.motortrend.com/files/65a2b04ba4170a0009bc41ee/2022-ram-1500-trx-ignition-edition-exterior.jpg?w=768&width=768&q=75&format=webp',
     'Transporter', 'Jahreswagen', true, 'Manuell', 'Heckantrieb', 'Silber')
  ];
  
  motorcycles: Motorcycle[] = [
    new Motorcycle(1, 'Ducati', 'Panigale V4', 25000, 5000, new Date(2021, 2, 8), 'Benzin', 214, 'Sport Motorcycle', 'https://images5.1000ps.net/images_bikekat/2025/5-Ducati/9182-Panigale_V4_S/005-638575757837101304-ducati-panigale-v4-s.jpg?height=450&width=730&mode=crop', 1103, 4, 'Cafe Racer'),
    new Motorcycle(2, 'Yamaha', 'MT-09', 10000, 10000, new Date(2020, 6, 15), 'Benzin', 117, 'Naked Bike', 'https://cdn.motochecker.at/motorrad/yamaha-mt-09-2021-0.png', 847, 3, 'Naked Bike')
  ];
  

  caravans: Caravan[] = [
    new Caravan(1, 'Hymer', 'B-Class', 90000, 30000, new Date(2017, 4, 22), 'Diesel', 200, 'Luxury Motorhome', 'data:image/jpeg;base64,', 'Teilintegriertes Wohnmobil',
     'Gebrauchtwagen', true, 'Weiß'),
    new Caravan(2, 'Adria', 'Twin Plus', 75000, 25000, new Date(2018, 9, 18), 'Diesel', 150, 'Compact Campervan', 'data:image/jpeg;base64,', 'Kastenwagen', 'Gebrauchtwagen', true, 'Grau')
  ];

  // Filtered Arrays
  filteredVehicles: { title: string; vehicles: Vehicle[] }[] = [
    { title: 'Cars', vehicles: [...this.cars] },
    { title: 'Trucks', vehicles: [...this.trucks] },
    { title: 'Motorcycles', vehicles: [...this.motorcycles] },
    { title: 'Caravans', vehicles: [...this.caravans] }
  ];

  applyFilters() {
    this.filteredVehicles.forEach(group => {
      group.vehicles = this.applyFilter(group.vehicles);
    });
  }

  private applyFilter<T extends Vehicle>(vehicles: T[]): T[] {
    return vehicles.filter(vehicle => {
      const matchesBrand = this.filters.brand ? vehicle.brand.toLowerCase().includes(this.filters.brand.toLowerCase()) : true;
      const matchesModel = this.filters.model ? vehicle.model.toLowerCase().includes(this.filters.model.toLowerCase()) : true;
      const matchesPrice = this.filters.maxPrice ? vehicle.price <= this.filters.maxPrice : true;
      const matchesMileage = this.filters.maxMileage ? vehicle.mileage <= this.filters.maxMileage : true;
      const matchesYear = this.filters.minYear ? vehicle.firstRegistration.getFullYear() >= this.filters.minYear : true;
      return matchesBrand && matchesModel && matchesPrice && matchesMileage && matchesYear;
    });
  }
}
